pragma solidity >=0.6.0;
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";


import "./libraries/SafeBEP20.sol";
import "./interfaces/IReferralReward.sol";

contract ReferralReward is IReferralReward, Ownable {
    using SafeMath for uint256;
    using SafeBEP20 for IBEP20;

    IBEP20 private _token;
    bool private _paused;

    uint256 private _amount;
    address private _wallet;


    mapping(address => address) _rewards;
    mapping(address => uint256) _balances;
    mapping(int8 => UserType) private _userTypes;
    mapping(address => int8) private _users;
    struct UserType {
        uint256 amount;
        address wallet;
    }

    function balanceOf(address account) public override view returns (uint256) {
        return _balances[account];
    }

    constructor(IBEP20 token, uint256 defaultAmount, address defaultWallet) public {
        require(address(token) != address(0), "ReferralReward: token is the zero address");
        require(defaultWallet != address(0), "ReferralReward: defaultWallet is the zero address");
        require(defaultAmount != 0, "ReferralReward: defaultAmount is zero");

        _token = token;
        _amount = defaultAmount;
        _wallet = defaultWallet;
        _paused = false;
    }

    function setUserType(int8 typeId, address wallet, uint256 amount) public  onlyOwner
    {
        require(wallet != address(0), "ReferralReward: wallet is the zero address");

        _userTypes[typeId].wallet = wallet;
        _userTypes[typeId].amount = amount;
    }

    function setUser(address userAddress, int8 typeId) public  onlyOwner
    {
        require(userAddress != address(0), "ReferralReward: userAddress is the zero address");
        _users[userAddress] = typeId;
    }

    function getUser(address userAddress) public view virtual returns (int8 userType, uint256 amount, address wallet) {
        int8 _userType = _users[userAddress];
        if (_userType==0)
            return (0, _amount, _wallet);

        UserType memory t = _userTypes[_users[userAddress]];
        return (_userType, t.amount, t.wallet);
    }

    function wallet() public view virtual returns (address) {
        return _wallet;
    }
    function amount() public view virtual returns (uint256) {
        return _amount;
    }


    function setWallet(address wallet) public onlyOwner
    {
        require(wallet != address(0), "ReferralReward: wallet is the zero address");
        _wallet = wallet;
    }
    function setAmount(address wallet, uint256 amount) public  onlyOwner
    {
        _amount = amount;
    }

    function paused() public view virtual returns (bool) {
        return _paused;
    }

    function pause() onlyOwner public {
        _paused = true;
    }
    function unpause() onlyOwner public {
        _paused = false;
    }

    function transfer(address sender, address recipient) external override onlyOwner {
        require(sender != address(0), 'ReferralReward: transfer from the zero address');
        if (_paused || _rewards[sender] != address(0))
            return;

        require(recipient != address(0), 'ReferralReward: transfer to the zero address');


        uint256 amount = _amount;
        address wallet = _wallet;

        UserType memory userType = _userTypes[_users[recipient]];
        if (userType.amount != 0)
        {
            amount = userType.amount;
            wallet = userType.wallet;
        }

        //_token.safeApprove(recipient, amount);
        _token.safeTransferFrom(wallet, address(this), amount);
        _token.safeTransfer(recipient, amount);

        _balances[recipient] = _balances[recipient].add(amount);
        _rewards[sender] = recipient;
    }

}
